import React, { Component } from 'react'

class TodoItem extends Component {

    constructor(props) {
        super(props);
        this.handleCLick = this.handleCLick.bind(this);
    }

    render() {
        return (
            <div onClick={this.handleClick}>
                {this.props.content}
            </div>
        )
    }

    handleClick() {
        alert(this.props.index)
    }
}

export default TodoItem;
